var LocationProvider,
	browser = require( '../mobile.startup/Browser' ).getSingleton(),
	util = require( '../mobile.startup/util' );

/**
 * API for retrieving location from user device
 * @class LocationProvider
 */
LocationProvider = {
	/**
	 * Detect if browser supports geolocation
	 * @memberof LocationProvider
	 * @instance
	 * @return {boolean}
	 */
	isAvailable: function () {
		return browser.supportsGeoLocation();
	},

	/**
	 * Obtain users current location and return a deferred object with the
	 * longitude and latitude values
	 * Resolve return object with 'incompatible' if browser doesn't support geo location
	 * @memberof LocationProvider
	 * @instance
	 * @return {jQuery.Deferred}
	 */
	getCurrentPosition: function () {
		var result = util.Deferred();
		if ( LocationProvider.isAvailable() ) {
			navigator.geolocation.getCurrentPosition(
				function ( geo ) {
					result.resolve( {
						latitude: geo.coords.latitude,
						longitude: geo.coords.longitude
					} );
				},
				function ( err ) {
					var error;
					switch ( err.code ) {
						case err.PERMISSION_DENIED:
							error = 'permission';
							break;
						case err.TIMEOUT:
							error = 'timeout';
							break;
						case err.POSITION_UNAVAILABLE:
							error = 'location';
							break;
						default:
							error = 'unknown';
					}
					result.reject( error );
				},
				{
					timeout: 10000,
					enableHighAccuracy: true
				}
			);
		} else {
			result.reject( 'incompatible' );
		}
		return result;
	}
};

module.exports = LocationProvider;
/** @event */
var
	NEARBY_EVENT_POST_RENDER = 'Nearby-postRender',
	MessageBox = require( '../mobile.startup/MessageBox' ),
	NearbyGateway = require( './NearbyGateway' ),
	util = require( '../mobile.startup/util' ),
	WatchstarPageList = require( '../mobile.startup/watchstar/WatchstarPageList.js' ),
	mfExtend = require( '../mobile.startup/mfExtend' );

/**
 * List of nearby pages
 * @class Nearby
 * @uses NearbyGateway
 * @extends WatchstarPageList
 *
 * @param {Object} options Configuration options
 * @param {OO.EventEmitter} options.eventBus Object used to emit Nearby-postRender events
 * @param {Function} [options.onItemClick] Callback invoked when a result is
 *                                         clicked. Defaults to nop.
 */
function Nearby( options ) {
	var self = this,
		_super = WatchstarPageList;

	this.range = options.range || mw.config.get( 'wgMFNearbyRange' ) || 1000;
	this.source = options.source || 'nearby';
	this.nearbyApi = new NearbyGateway( {
		api: options.api
	} );
	this.eventBus = options.eventBus;

	if ( options.errorType ) {
		options.errorOptions = self._errorOptions( options.errorType );
	}

	this.onItemClick = options.onItemClick;

	_super.apply( this, arguments );
}

mfExtend( Nearby, WatchstarPageList, {
	/**
	 * @memberof Nearby
	 * @instance
	 */
	errorMessages: {
		empty: {
			heading: mw.msg( 'mobile-frontend-nearby-noresults' ),
			hasHeading: true,
			msg: mw.msg( 'mobile-frontend-nearby-noresults-guidance' )
		},
		http: {
			heading: mw.msg( 'mobile-frontend-nearby-error' ),
			hasHeading: true,
			msg: mw.msg( 'mobile-frontend-nearby-error-guidance' )
		},
		incompatible: {
			heading: mw.msg( 'mobile-frontend-nearby-requirements' ),
			hasHeading: true,
			msg: mw.msg( 'mobile-frontend-nearby-requirements-guidance' )
		}
	},
	/**
	 * @memberof Nearby
	 * @instance
	 */
	templatePartials: util.extend( {}, WatchstarPageList.prototype.templatePartials, {
		pageList: WatchstarPageList.prototype.template
	} ),
	/**
	 * @memberof Nearby
	 * @instance
	 */
	template: util.template( `
{{{spinner}}}
{{>pageList}}
	` ),
	/**
	 * @memberof Nearby
	 * @instance
	 * @mixes WatchstarPageList#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {mw.Api} defaults.api
	 * @property {Object} defaults.errorOptions options to pass to a messagebox template
	 * tells the user that their location is being looked up
	 */
	defaults: util.extend( {}, WatchstarPageList.prototype.defaults, {
		errorOptions: undefined
	} ),
	/**
	 * Request pages from api based on provided options.
	 * When options.longitude and options.latitude set getPages near that location.
	 * If those are not present use options.title to find pages near that title.
	 * If no valid options given resolve return object with error message.
	 * @memberof Nearby
	 * @instance
	 * @param {Object} options Configuration options
	 * @return {jQuery.Deferred}
	 * @private
	 */
	_find: function ( options ) {
		var result = util.Deferred(),
			self = this;

		/**
		 * Handler for successful query
		 * @param {Array} pages as passed by then callback of Nearby##getPages
		 */
		function pagesSuccess( pages ) {
			options.pages = pages;
			if ( pages && pages.length === 0 ) {
				options.errorOptions = self._errorOptions( 'empty' );
			}
			self._isLoading = false;
			result.resolve( options );
		}

		/**
		 * Handler for failed query
		 *
		 * @param {string} code Error Code
		 */
		function pagesError( code ) {
			self._isLoading = false;
			options.errorOptions = self._errorOptions( code );
			result.resolve( options );
		}

		if ( options.latitude && options.longitude ) {
			this.nearbyApi.getPages(
				{
					latitude: options.latitude,
					longitude: options.longitude
				},
				this.range, options.exclude
			).then( pagesSuccess, pagesError );
		} else if ( options.pageTitle ) {
			this.nearbyApi.getPagesAroundPage( options.pageTitle, this.range )
				.then( pagesSuccess, pagesError );
		} else {
			if ( options.errorType ) {
				options.errorOptions = this._errorOptions( options.errorType );
			}
			result.resolve( options );
		}
		return result;
	},
	/**
	 * Generate a list of options that can be passed to a messagebox template.
	 * @memberof Nearby
	 * @instance
	 * @private
	 * @param {string} key to a defined error message
	 * @param {string} msg Message to use, instead of a mapped error message
	 *  from this.errorMessages
	 * @return {Object}
	 */
	_errorOptions: function ( key ) {
		var message = this.errorMessages[ key ] || this.errorMessages.http;
		return util.extend( {
			className: 'errorbox'
		}, message );
	},
	/**
	 * @inheritdoc
	 * @memberof Nearby
	 * @instance
	 */
	postRender: function () {
		var self = this,
			$messageBox;

		if ( !this._isLoading ) {
			this.$el.find( '.page-list' ).removeClass( 'hidden' );
		}
		WatchstarPageList.prototype.postRender.apply( this );
		this._postRenderLinks();

		if ( this.options.errorOptions ) {
			$messageBox = new MessageBox( this.options.errorOptions );
			$messageBox.appendTo( this.$el );
		}

		// FIXME: This should probably just be $( function () {} )
		this.$el.find( function () {
			// todo: use the local emitter when refresh() doesn't recreate the
			//       OO.EventEmitter by calling the super's constructor.
			self.eventBus.emit( NEARBY_EVENT_POST_RENDER );
		} );
	},
	/**
	 * Hijack links to apply several customisations to them:
	 * Ensure that when clicked they register an uploads funnel.
	 * Ensure that when a user navigates back to the page their page position is restored using
	 * fragment identifier trickery.
	 * @private
	 * @memberof Nearby
	 * @instance
	 */
	_postRenderLinks: function () {
		var self = this;
		this.$el.find( 'a' ).each( function ( i ) {
			// FIXME: not unique if multiple Nearby objects on same page
			self.$el.find( this ).attr( 'id', 'nearby-page-list-item-' + i );
		} ).on( 'click', this.onItemClick );
	},
	/**
	 * Refresh the list of the nearby articles depending on the options.
	 * The current location, latitude/longitude, or page title can be used
	 * to find the articles.
	 * @memberof Nearby
	 * @instance
	 * @param {Object} options Configuration options
	 * @throws {Error} If Nearby has not been initialised correctly.
	 * @return {jQuery.Deferred}
	 */
	refresh: function ( options ) {
		var self = this,
			_super = WatchstarPageList;

		this.$el.find( '.page-list' ).addClass( 'hidden' );
		// Run it once for loader etc
		this._isLoading = true;
		if ( ( options.latitude && options.longitude ) || options.pageTitle ) {
			// Flush any existing list of pages
			options.pages = [];

			// Get some new pages
			return this._find( options ).then( function ( options ) {
				_super.call( self, options );
			}, function ( errorType ) {
				options.errorOptions = self._errorOptions( errorType );
				self._isLoading = false;
				_super.call( self, options );
			} );
		} else {
			throw new Error( 'No title or longitude, latitude options have been passed' );
		}
	}
} );

module.exports = Nearby;
var limit = 50,
	pageJSONParser = require( '../mobile.startup/page/pageJSONParser' ),
	ns = mw.config.get( 'wgContentNamespaces' ),
	util = require( '../mobile.startup/util' ),
	extendSearchParams = require( '../mobile.startup/extendSearchParams' );

/**
 * API for retrieving nearby pages
 * @class NearbyGateway
 * @param {Object} options Configuration options
 * @param {mw.Api} options.api
 */
function NearbyGateway( options ) {
	this.api = options.api;
}

NearbyGateway.prototype = {
	/**
	 * Returns a human readable string stating the distance in meters or kilometers
	 * depending on size.
	 * @memberof NearbyGateway
	 * @instance
	 * @private
	 * @param {number} d The distance in meters.
	 * @return {string} message stating how far the user is from the point of interest.
	 */
	_distanceMessage: function ( d ) {
		if ( d < 1 ) {
			d *= 100;
			d = Math.ceil( d ) * 10;
			if ( d === 1000 ) {
				d = 1;
			} else {
				return mw.msg( 'mobile-frontend-nearby-distance-meters', mw.language.convertNumber( d ) );
			}
		} else {
			if ( d > 2 ) {
				d *= 10;
				d = Math.ceil( d ) / 10;
				d = d.toFixed( 1 );
			} else {
				d *= 100;
				d = Math.ceil( d ) / 100;
				d = d.toFixed( 2 );
			}
		}
		return mw.msg( 'mobile-frontend-nearby-distance', mw.language.convertNumber( d ) );
	},
	/**
	 * Returns a list of pages around a given point
	 * @memberof NearbyGateway
	 * @instance
	 * @param {Object} coords In form { latitude: 0, longitude: 2 }
	 * @param {number} range Number of meters to perform a geosearch for
	 * @param {string} exclude Name of a title to exclude from the list of results
	 * @return {jQuery.Deferred} Object taking list of pages as argument
	 */
	getPages: function ( coords, range, exclude ) {
		return this._search( {
			ggscoord: [ coords.latitude, coords.longitude ]
		}, range, exclude );
	},

	/**
	 * Gets the pages around a page. It excludes itself from the search
	 * @memberof NearbyGateway
	 * @instance
	 * @param {string} page Page title like "W_San_Francisco"
	 * @param {number} range Number of meters to perform a geosearch for
	 * @return {jQuery.Deferred} Object taking list of pages as argument
	 */
	getPagesAroundPage: function ( page, range ) {
		return this._search( {
			ggspage: page
		}, range, page );
	},

	/**
	 * Searches for pages nearby
	 * @memberof NearbyGateway
	 * @instance
	 * @private
	 * @param {Object} params Parameters to use for searching
	 * @param {number} range Number of meters to perform a geosearch for
	 * @param {string} exclude Name of a title to exclude from the list of results
	 * @return {jQuery.Deferred} Object taking list of pages as argument
	 */
	_search: function ( params, range, exclude ) {
		var requestParams,
			d = util.Deferred(),
			self = this;

		requestParams = extendSearchParams( 'nearby', {
			colimit: 'max',
			prop: [ 'coordinates' ],
			generator: 'geosearch',
			ggsradius: range,
			ggsnamespace: ns,
			ggslimit: limit
		}, params );

		if ( params.ggscoord ) {
			requestParams.codistancefrompoint = params.ggscoord;
		} else if ( params.ggspage ) {
			requestParams.codistancefrompage = params.ggspage;
		}

		this.api.ajax( requestParams ).then( function ( resp ) {
			var pages;

			// resp.query.pages is an Array<Page> instead of a map like in other
			// API requests
			if ( resp.query ) {
				pages = resp.query.pages || [];
			} else {
				pages = [];
			}

			pages = pages.map( function ( page, i ) {
				var coords, p;
				p = pageJSONParser.parse( page );
				p.anchor = 'item_' + i;

				// protect against declined bug T49133
				if ( page.coordinates ) {
					coords = page.coordinates[0];
					// FIXME: Make part of the Page object
					p.dist = coords.dist / 1000;
					p.latitude = coords.lat;
					p.longitude = coords.lon;
					p.proximity = self._distanceMessage( p.dist );
				} else {
					p.dist = 0;
				}
				if ( exclude !== page.title ) {
					return p;
				} else {
					return null;
				}
			} ).filter( function ( page ) { return !!page; } );

			pages.sort( function ( a, b ) {
				return a.dist > b.dist ? 1 : -1;
			} );
			d.resolve( pages );
		}, function ( error ) {
			d.reject( error );
		} );
		return d;
	}
};

module.exports = NearbyGateway;
/* global $ */
/** @event */
var
	api = new mw.Api(),
	NEARBY_EVENT_POST_RENDER = 'Nearby-postRender',
	LocationProvider = require( './LocationProvider' ),
	loadingOverlay = require( '../mobile.startup/loadingOverlay' ),
	router = mw.loader.require( 'mediawiki.router' ),
	Nearby = require( './Nearby' ),
	util = require( '../mobile.startup/util' ),
	$infoContainer = $( '#mf-nearby-info-holder' ),
	eventBus = new OO.EventEmitter(),
	nearby,
	options = {
		eventBus: eventBus,
		el: $( '#mw-mf-nearby' ),
		funnel: 'nearby',
		onItemClick: function ( ev ) {
			if ( !util.isModifiedEvent( ev ) && !isPageOrCoordFragment( router.getPath() ) ) {
				// Change the URL fragment to the clicked element so that back
				// navigation can retain the item position. This behavior is
				// unwanted for results displayed around a page or coordinate since
				// that information is stored in the hash and would be overwritten.
				router.navigate( $( this ).attr( 'id' ) );
			}
		}
	},
	overlay = loadingOverlay();

/**
 * Show the title and hide the info container
 */
function hideInitialScreen() {
	$infoContainer.remove();
	$( 'body' ).removeClass( 'nearby-accept-pending' );
}
/**
 * @param {string} fragment The URL fragment.
 * @return {boolean} True if the current URL is based around page or
 *                   coordinates (as opposed to current location or search).
 *                   e.g.: Special:Nearby#/page/San_Francisco and
 *                   Special:Nearby#/coord/0,0.
 */
function isPageOrCoordFragment( fragment ) {
	return fragment.match( /^(\/page|\/coord)/ );
}

/**
 * @param {string} fragment The URL fragment.
 * @return {boolean} True if the current URL doesn't contain an invalid
 *                   identifier expression, such as the slash in
 *                   Special:Nearby#/search, and probably contains the
 *                   target identifier to scroll to.
 */
function isFragmentIdentifier( fragment ) {
	return fragment && fragment.indexOf( '/' ) === -1;
}

/**
 * Initialize or instantiate Nearby with options
 * @param {Object} opt
 * @param {mw.Api} opt.api instance
 */
function refresh( opt ) {
	// make sure, that the api object (if created above) is added to the options object used
	// in the Nearby module
	opt = util.extend( {}, opt, options );

	if ( !nearby ) {
		nearby = new Nearby( opt );
		// todo: use the local emitter when refresh() doesn't recreate the
		//       OO.EventEmitter by calling the super's constructor.
		eventBus.on( NEARBY_EVENT_POST_RENDER, function () {
			var fragment = router.getPath(), el;
			if ( isFragmentIdentifier( fragment ) ) {
				// The hash is expected to be an identifier selector (unless the
				// user entered rubbish).
				el = nearby.$el.find( '#' + fragment );
				if ( el[0] && el[0].nodeType ) {
					$( window ).scrollTop( el.offset().top );
				}
			}
			overlay.hide();
		} );
	}
	nearby.refresh( opt );
}

// Routing on the nearby view

/*
 * #/coords/lat,long
 */
router.route( /^\/coord\/(-?\d+(?:\.\d+)?),(-?\d+(?:\.\d+)?)/, function ( lat, lon ) {
	// TODO this is bad, route shouldn't have side effects but otherwise mediawiki router
	// cannot refresh the page. We need to store those so when user hits $refreshButton
	// we reload the results
	options.latitude = lat;
	options.longitude = lon;

	hideInitialScreen();
	// Search with coordinates
	refresh( util.extend( options, { api: api } ) );
} );

/*
 * #/page/PageTitle
 */
router.route( /^\/page\/(.+)$/, function ( pageTitle ) {
	hideInitialScreen();
	overlay.hide();
	refresh( util.extend( {}, options, {
		api: api,
		pageTitle: mw.Uri.decode( pageTitle )
	} ) );
} );

router.checkRoute();
$( '#showArticles' ).on( 'click', function () {
	overlay.show();
	LocationProvider.getCurrentPosition().then( function ( geo ) {
		router.navigate( '#/coord/' + geo.latitude + ',' + geo.longitude );
	} ).catch( function ( error ) {
		overlay.hide();
		// We want to show the Alert dialog to make sure user sees it
		switch ( error ) {
			case 'permission':
				alert( mw.msg( 'mobile-frontend-nearby-permission-denied' ) );
				break;
			case 'location':
				alert( mw.msg( 'mobile-frontend-nearby-location-unavailable' ) );
				break;
			default:
				// timeout or undefined, do nothing for now
		}
	} );
} );
