const util = require( '../mobile.startup/util.js' ),
	View = require( '../mobile.startup/View' ),
	browser = require( '../mobile.startup/Browser' ).getSingleton();

/**
 * List of items page view
 */
class PageList extends View {
	constructor( params ) {
		super( params );
	}

	/**
	 * @mixes module:mobile.startup/View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {Page[]} defaults.pages Array of Page objects. These should match
	 *                              the Page model and not necessarily the
	 *                              underlying API format used.
	 * E.g. [
	 *   {
	 *     heading: "<strong>C</strong>laude Monet",
	 *     id: undefined,
	 *     title: "Claude Monet",
	 *     displayTitle: "<i>Claude Monet</i>",
	 *     url: "/wiki/Claude_Monet",
	 *     thumbnail: {
	 *       height: 62,
	 *       source: "http://127.0.0.1:8080/images/thumb/thumb.jpg",
	 *       width: 80,
	 *       isLandscape: true
	 *     }
	 *   }
	 * ]
	 */
	get defaults() {
		return {
			pages: []
		};
	}

	/**
	 * Render page images for the existing page list. Assumes no page images have been loaded.
	 */
	renderPageImages() {
		setTimeout( () => {
			this.$el.find( '.list-thumb' ).each( ( i, thumbEl ) => {
				const style = this.$el.find( thumbEl ).data( 'style' );
				this.$el.find( thumbEl ).attr( 'style', style );
			} );
			// Delay an unnecessary load of images on mobile (slower?) connections
			// In particular on search results which can be regenerated quickly.
		}, browser.isWideScreen() ? 0 : 1000 );
	}

	/**
	 * @inheritdoc
	 */
	postRender() {
		this.renderPageImages();
	}

	get template() {
		return util.template( `
<ul class="mw-mf-page-list thumbs actionable">
	{{#pages}}
		{{>item}}
	{{/pages}}
</ul>
	` );
	}

	get templatePartials() {
		return {
			// The server uses a very different structure in
			// SpecialMobileEditWatchlist.getLineHtml(). Be aware of these differences
			// when updating server rendered items.
			item: util.template( `
	<li title="{{title}}" data-id="{{id}}" class="page-summary">
	<a href="{{url}}" class="title {{#isMissing}}new{{/isMissing}}"
		{{#anchor}}name="{{anchor}}"{{/anchor}}
		data-title="{{title}}">
		<div class="list-thumb
		{{#thumbnail.isLandscape}}list-thumb-y{{/thumbnail.isLandscape}}
		{{^thumbnail.isLandscape}}list-thumb-x{{/thumbnail.isLandscape}}"
		{{#thumbnail}}data-style="background-image: url( {{thumbnail.source}} )"{{/thumbnail}}>
		{{^thumbnail}}<span class="mf-icon-image"></span>{{/thumbnail}}
		</div>
		<h3>{{{displayTitle}}}</h3>
		{{#wikidataDescription}}
		<div class="wikidata-description">{{wikidataDescription}}</div>
		{{/wikidataDescription}}
	</a>
	</li>
		` )
		};
	}
}

module.exports = PageList;
const util = require( '../mobile.startup/util' );

/**
 * Class to assist a view in implementing infinite scrolling on some DOM
 * element. This module itself is only responsible for emitting an Event when
 * the bottom of an Element is scrolled to.
 *
 * @class ScrollEndEventEmitter
 * @mixes OO.EventEmitter
 *
 * Use this class in a view to help it do infinite scrolling.
 *
 * 1. Initialize it in the constructor `initialize` and listen to the
 *   EVENT_SCROLL_END event it emits (and call your loading function then)
 * 2. On preRender (once we have the DOM element) set it into the infinite
 *   scrolling object and disable it until we've loaded.
 * 3. Once you have loaded the list and put it in the DOM, enable the
 *   infinite scrolling detection.
 *   - Every time the scroller detection triggers a load, it auto disables
 *     to not trigger multiple times. After you have loaded, manually
 *     re-enable it.
 *
 * Example:
 *     @example
 *     <code>
 *       var
 *         ScrollEndEventEmitter = require( './ScrollEndEventEmitter' ),
 *         eventBus = require( './eventBusSingleton' );
 *       class PhotoList extends View {
 *         //...
 *         initialize: function ( options ) {
 *           this.gateway = new PhotoListGateway( {
 *             username: options.username
 *           } );
 *           // 1. Set up infinite scroll helper and listen to events
 *           this.scrollEndEventEmitter = new ScrollEndEventEmitter( eventBus, 1000 );
 *           this.scrollEndEventEmitter.on( ScrollEndEventEmitter.EVENT_SCROLL_END,
 *             this._loadPhotos.bind( this ) );
 *           super.initialize( options );
 *         },
 *         preRender: function () {
 *           // 2. Disable until we've got the list rendered and set DOM el
 *           this.scrollEndEventEmitter.setElement( this.$el );
 *           this.scrollEndEventEmitter.disable();
 *         },
 *         _loadPhotos: function () {
 *           this.gateway.getPhotos().then( ( photos ) => {
 *             // load photos into the DOM ...
 *             // 3. and (re-)enable infinite scrolling
 *             this.scrollEndEventEmitter.enable();
 *           } );
 *         }
 *       }
 *     </code>
 *
 * @fires ScrollEndEventEmitter#ScrollEndEventEmitter-scrollEnd
 */
class ScrollEndEventEmitter {
	/**
	 * @param {Object} eventBus object to listen for scroll:throttled events
	 * @param {number} [threshold=100] distance in pixels used to calculate if scroll
	 * position is near the end of the $el
	 */
	constructor( eventBus, threshold ) {
		this.threshold = threshold || 100;
		this.eventBus = eventBus;
		this.enable();
		OO.EventEmitter.call( this );
	}

	/**
	 * Listen to scroll on window and notify this._onScroll
	 *
	 * @private
	 */
	_bindScroll() {
		if ( !this._scrollHandler ) {
			this._scrollHandler = () => this._onScroll();
			this.eventBus.on( 'scroll:throttled', this._scrollHandler );
		}
	}

	/**
	 * Unbind scroll handler
	 *
	 * @private
	 */
	_unbindScroll() {
		if ( this._scrollHandler ) {
			this.eventBus.off( 'scroll:throttled', this._scrollHandler );
			this._scrollHandler = null;
		}
	}

	/**
	 * Scroll handler. Triggers load event when near the end of the container.
	 *
	 * @private
	 */
	_onScroll() {
		if ( this.$el && this.enabled && this.scrollNearEnd() ) {
			// Disable when triggering an event. Won't trigger again until
			// re-enabled.
			this.disable();
			this.emit( ScrollEndEventEmitter.EVENT_SCROLL_END );
		}
	}

	/**
	 * Is the scroll position near the end of the container element?
	 *
	 * @private
	 * @return {boolean}
	 */
	scrollNearEnd() {
		if ( !this.$el || !this.$el.offset() ) {
			return false;
		}
		const $window = util.getWindow(),
			scrollBottom = $window.scrollTop() + $window.height(),
			endPosition = this.$el.offset().top + this.$el.outerHeight();
		return scrollBottom + this.threshold > endPosition;
	}

	/**
	 * Enable the ScrollEndEventEmitter so that it triggers events.
	 */
	enable() {
		this.enabled = true;
		this._bindScroll();
	}

	/**
	 * Disable the ScrollEndEventEmitter so that it doesn't trigger events.
	 */
	disable() {
		this.enabled = false;
		this._unbindScroll();
	}

	/**
	 * Set the element to compare to scroll position to
	 *
	 * @param {jQuery.Object} $el jQuery element where we want to listen for
	 * scroll end.
	 */
	setElement( $el ) {
		this.$el = $el;
	}
}
OO.mixinClass( ScrollEndEventEmitter, OO.EventEmitter );

/**
 * Fired when scroll bottom has been reached.
 *
 * @event ScrollEndEventEmitter#ScrollEndEventEmitter-scrollEnd
 */
ScrollEndEventEmitter.EVENT_SCROLL_END = 'ScrollEndEventEmitter-scrollEnd';

module.exports = ScrollEndEventEmitter;
const
	PageList = require( './PageList' ),
	WatchstarPageList = require( './WatchstarPageList' ),
	ScrollEndEventEmitter = require( './ScrollEndEventEmitter' ),
	util = require( '../mobile.startup/util' ),
	WatchListGateway = require( './WatchListGateway' );

/**
 * An extension of the WatchstarPageList which preloads pages as all being
 * watched.
 *
 * @uses ScrollEndEventEmitter
 *
 * @fires watched
 * @fires watch
 * @private
 */
class WatchList extends WatchstarPageList {
	/**
	 * @param {Object} params Configuration options
	 * @param {OO.EventEmitter} params.eventBus Object used to listen for scroll:throttled events
	 */
	constructor( params ) {
		super( util.extend(
			{},
			{
				isBorderBox: false
			},
			params
		) );
	}

	initialize( options ) {
		// Set up infinite scroll helper and listen to events
		this.scrollEndEventEmitter = new ScrollEndEventEmitter( options.eventBus );
		this.scrollEndEventEmitter.on( ScrollEndEventEmitter.EVENT_SCROLL_END,
			() => this._loadPages() );

		let lastTitle;
		if ( options.el ) {
			lastTitle = this.getLastTitle( options.el );
		}
		this.gateway = new WatchListGateway( options.api, lastTitle );
		super.initialize( options );
	}

	preRender() {
		// The DOM will be modified. Prevent any false scroll end events from
		// being emitted.
		this.scrollEndEventEmitter.disable();
		this.scrollEndEventEmitter.setElement( this.$el );
	}

	/**
	 * Also sets a watch uploads funnel.
	 *
	 * @inheritdoc
	 */
	postRender() {
		// Skip a level from WatchstarPageList directly to PageList.
		PageList.prototype.postRender.apply( this );

		const $items = this.queryUnitializedItems();

		// WatchList requests list of watched pages. The list contains only
		// watched pages so it's safe to transform the title map to a status map
		// with each entry marked watched (true).
		const statuses = Object.keys( this.parsePagesFromItems( $items ) )
			.reduce( ( arr, title ) => {
				arr[ title ] = true;
				return arr;
			}, {} );
		this.renderItems( $items, statuses );

		// The list has been extended. Re-enable scroll end events.
		this.scrollEndEventEmitter.enable();
	}

	/**
	 * Loads pages from the api and triggers render.
	 * Infinite scroll is re-enabled in postRender.
	 *
	 */
	_loadPages() {
		this.gateway.loadWatchlist().then( ( pages ) => {
			pages.forEach( ( page ) => {
				this.appendPage( page );
			} );
			this.render();
		} );
	}

	/**
	 * Appends a list item
	 *
	 * @param {Page} page
	 */
	appendPage( page ) {
		// wikidata descriptions should not show in this view.
		const templateOptions = util.extend( {}, page, {
			wikidataDescription: undefined
		} );
		this.$el.append( this.templatePartials.item.render( templateOptions ) );
	}

	/**
	 * Get the last title from the rendered HTML.
	 * Used for initializing the API
	 *
	 * @param {jQuery.Object} $el Dom element of the list
	 * @return {string}
	 */
	getLastTitle( $el ) {
		return $el.find( 'li' ).last().attr( 'title' );
	}
}

module.exports = WatchList;
const
	pageJSONParser = require( './pageJSONParser' ),
	util = require( '../mobile.startup/util' ),
	extendSearchParams = require( './extendSearchParams' );

/**
 * API for interacting with watchlist.
 */
class WatchListGateway {
	/**
	 * @param {mw.Api} api
	 * @param {string} lastTitle of page listed in Watchlist to be used as a continuation parameter
	 * @private
	 */
	constructor( api, lastTitle ) {
		this.api = api;
		// Try to keep it in sync with SpecialMobileEditWatchlist::LIMIT (php)
		this.limit = 50;

		if ( lastTitle ) {
			this.continueParams = {
				continue: 'gwrcontinue||',
				gwrcontinue: '0|' + lastTitle.replace( / /g, '_' )
			};
			this.shouldSkipFirstTitle = true;
		} else {
			this.continueParams = {
				continue: ''
			};
			this.shouldSkipFirstTitle = false;
		}

		this.canContinue = true;
	}

	/**
	 * Load the list of items on the watchlist
	 *
	 * @return {jQuery.Deferred}
	 */
	loadWatchlist() {
		const params = extendSearchParams( 'watchlist', {
			prop: [ 'info', 'revisions' ],
			rvprop: 'timestamp|user',
			generator: 'watchlistraw',
			gwrnamespace: '0',
			gwrlimit: this.limit
		}, this.continueParams );

		if ( this.canContinue === false ) {
			return util.Deferred().resolve( [] );
		}
		return this.api.get( params ).then( ( data ) => {
			if ( data.continue !== undefined ) {
				this.continueParams = data.continue;
			} else {
				this.canContinue = false;
			}

			return this.parseData( data );
		} );
	}

	/**
	 * Parse api response data into pagelist item format
	 *
	 * @param {Object[]} data
	 * @return {Page[]}
	 */
	parseData( data ) {
		let pages;

		if ( !data.query || !data.query.pages ) {
			return [];
		}

		pages = data.query.pages;

		// Sort results alphabetically (the api map doesn't have any order). The
		// watchlist is ordered alphabetically right now.
		pages.sort( ( p1, p2 ) => p1.title === p2.title ? 0 : ( p1.title < p2.title ? -1 : 1 ) );

		// If we requested from the last item of the previous page, we shall
		// remove the first result (to avoid it being repeated)
		if ( this.shouldSkipFirstTitle ) {
			pages = pages.slice( 1 );
			this.shouldSkipFirstTitle = false;
		}

		// Transform the items to a sensible format
		return pages.map( pageJSONParser.parse );
	}
}

module.exports = WatchListGateway;
const util = require( '../mobile.startup/util' ),
	actionParams = require( '../mobile.startup/actionParams' );

/**
 * @ignore
 * @typedef {string|number} PageID Page ID. 0 / "0" is a special no-ID value.
 * {@link https://www.mediawiki.org/wiki/Manual:Page_table#page_id Page ID}
 *
 * @ignore
 * @typedef {string} PageTitle Canonical page title.
 * {@link https://www.mediawiki.org/wiki/Manual:Title.php#Canonical_forms Canonical forms}
 *
 * @ignore
 * @typedef {boolean} WatchStatus Page watch status; true if watched, false if
 *                                unwatched.
 * {@link https://www.mediawiki.org/wiki/API:Info API:Info} (see inprop.watched)
 * {@link https://www.mediawiki.org/wiki/API:Watch API:Watch} (see unwatch)
 *
 * @ignore
 * @typedef {Object.<PageTitle, WatchStatus>} WatchStatusMap
 */

/**
 * API for retrieving and modifying page watch statuses. This module interacts
 * with two endpoints, API:Info for GETs and API:Watch and for POSTs.
 */
class WatchstarGateway {
	/**
	 * @param {mw.Api} api
	 */
	constructor( api ) {
		this.api = api;
	}

	/**
	 * Issues zero to two asynchronous HTTP requests for the watch status of
	 * each page ID and title passed.
	 *
	 * Every watch entry has a title but not necessarily a page ID. Entries
	 * without IDs are missing pages, i.e., pages that do not exist. These
	 * entries are used to observe when a page with a given title is created.
	 * Although it is convenient to use titles because they're always present,
	 * IDs are preferred since they're far less likely to exceed the URL length
	 * limit.
	 *
	 * No request is issued when no IDs and no titles are passed. Given that the
	 * server state does not change between the two requests, overlapping title
	 * and ID members will behave as expected but there is no reason to issue
	 * such a request.
	 *
	 * @param {PageID[]} ids
	 * @param {PageTitle[]} titles
	 * @return {jQuery.Deferred<WatchStatusMap>}
	 */
	getStatuses( ids, titles ) {
		// Issue two requests and coalesce the results.
		return util.Promise.all( [
			this.getStatusesByID( ids ),
			this.getStatusesByTitle( titles )
		] ).then( function () {
			return util.extend.apply( util, arguments );
		} );
	}

	/**
	 * @param {PageID[]} ids
	 * @return {jQuery.Deferred<WatchStatusMap>}
	 */
	getStatusesByID( ids ) {
		if ( !ids.length ) {
			return util.Deferred().resolve( {} );
		}

		return this.api.get( {
			formatversion: 2,
			action: 'query',
			prop: 'info',
			inprop: 'watched',
			pageids: ids
		} ).then( ( rsp ) => this._unmarshalGetResponse( rsp ) );
	}

	/**
	 * @param {PageTitle[]} titles
	 * @return {jQuery.Deferred<WatchStatusMap>}
	 */
	getStatusesByTitle( titles ) {
		if ( !titles.length ) {
			return util.Deferred().resolve( {} );
		}

		return this.api.get( actionParams( {
			prop: 'info',
			inprop: 'watched',
			titles
		} ) ).then( ( rsp ) => this._unmarshalGetResponse( rsp ) );
	}

	/**
	 * @param {PageTitle[]} titles
	 * @param {WatchStatus} watched
	 * @return {jQuery.Deferred}
	 */
	postStatusesByTitle( titles, watched ) {
		const params = {
			action: 'watch',
			titles
		};
		if ( !watched ) {
			params.unwatch = !watched;
		}
		return this.api.postWithToken( 'watch', params );
	}

	/**
	 * @param {Object} rsp The API:Info response.
	 * @return {jQuery.Deferred<WatchStatusMap>}
	 * @see getStatusesByID
	 * @see getStatusesByTitle
	 * @ignore
	 */
	_unmarshalGetResponse( rsp ) {
		const pages = rsp && rsp.query && rsp.query.pages || [];
		return pages.reduce( ( statuses, page ) => {
			statuses[page.title] = page.watched;
			return statuses;
		}, {} );
	}
}

module.exports = WatchstarGateway;
const PageList = require( './PageList' ),
	watchstar = require( './watchstar' ),
	user = mw.user,
	util = require( '../mobile.startup/util' ),
	Page = require( '../mobile.startup/Page' ),
	WatchstarGateway = require( './WatchstarGateway' );

/**
 * @typedef {Object.<PageTitle, PageID>} PageTitleToPageIDMap
 * @ignore
 */

/**
 * List of items page view
 *
 * @uses Page
 * @uses WatchstarGateway
 * @uses Watchstar
 * @ignore
 *
 * @fires WatchstarPageList#unwatch
 * @fires WatchstarPageList#watch
 */
class WatchstarPageList extends PageList {

	/**
	 * @param {Object} options Configuration options
	 */
	constructor( options ) {
		super( options );
	}

	initialize( options ) {
		this.wsGateway = new WatchstarGateway( options.api );
		super.initialize( options );
	}

	/**
	 * @instance
	 * @mixes PageList#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {mw.Api} defaults.api
	 */
	postRender() {
		const
			ids = [],
			titles = [];

		super.postRender();

		const $items = this.queryUnitializedItems();
		const pages = this.parsePagesFromItems( $items );

		Object.keys( pages ).forEach( ( title ) => {
			const id = pages[title];
			// Favor IDs since they're short and unlikely to exceed URL length
			// limits when batched.
			if ( id && id !== '0' ) {
				// ID is present and valid.
				ids.push( id );
			} else {
				// Only titles are available for missing pages.
				titles.push( title );
			}
		} );

		return this.getPages( ids, titles )
			.then( ( statuses ) => this.renderItems( $items, statuses ) );
	}

	/**
	 * @param {jQuery.Element} $items
	 * @param {WatchStatusMap} statuses
	 * @ignore
	 */
	queryUnitializedItems() {
		return this.$el.find( 'li:not(.with-watchstar)' );
	}

	/**
	 * Retrieve pages
	 *
	 * @instance
	 * @param {PageID[]} ids
	 * @param {PageTitle[]} titles
	 * @return {jQuery.Deferred<WatchStatusMap>}
	 * @ignore
	 */
	getPages( ids, titles ) {
		// Rendering Watchstars for anonymous users is not useful. Short-circuit
		// the request.
		if ( user.isAnon() ) {
			return util.Deferred().resolve( {} );
		}

		return this.wsGateway.getStatuses( ids, titles );
	}

	/**
	 * @param {jQuery.Element} $items
	 * @return {PageTitleToPageIDMap}
	 * @ignore
	 */
	parsePagesFromItems( $items ) {
		const
			pages = {};
		$items.each( ( _, item ) => {
			const $item = this.$el.find( item );
			pages[$item.attr( 'title' )] = $item.data( 'id' );
		} );
		return pages;
	}

	/**
	 * @param {jQuery.Element} $items
	 * @param {WatchStatusMap} statuses
	 * @ignore
	 */
	renderItems( $items, statuses ) {
		// Rendering Watchstars for anonymous users is not useful. Nothing to do.
		if ( user.isAnon() ) {
			return;
		}

		// Create watch stars for each entry in list
		$items.each( ( _, item ) => {
			const
				$item = this.$el.find( item ),
				page = new Page( {
					// FIXME: Set sections so we don't hit the api (hacky)
					sections: [],
					title: $item.attr( 'title' ),
					id: $item.data( 'id' )
				} ),
				watched = statuses[page.getTitle()];

			this._appendWatchstar( $item, page, watched );
			$item.addClass( 'with-watchstar' );
		} );
	}

	/**
	 * @param {jQuery.Object} $item
	 * @param {Page} page
	 * @param {WatchStatus} watched
	 * @private
	 */
	_appendWatchstar( $item, page, watched ) {
		watchstar( {
			// WatchstarPageList.getPages() already retrieved the status of
			// each page. Explicitly set the watch state so another request
			// will not be issued by the Watchstar.
			isWatched: watched,
			page
		} ).appendTo( $item );
	}
}

module.exports = WatchstarPageList;
const util = require( '../mobile.startup/util.js' ),
	actionParams = require( '../mobile.startup/actionParams.js' );

/**
 * Extends the API query parameters to include those parameters required to also fetch Wikibase
 * descriptions and appropriately sized thumbnail images as well as those required to make a query.
 *
 * This function wraps `util.extend` with some Wikibase-specific configuration
 * variable management
 * but, like `util.extend`, is variadic and so can be used as a replacement for it in search
 * gateways, e.g.
 *
 * ```
 * var params = extendSearchParams(
 *   'search',
 *   baseParams,
 *   specializedParams,
 *   moreSpecializedParams
 * );
 * ```
 *
 * @param {string} feature The name of the feature
 * @throws {Error} If `feature` isn't one that shows Wikidata descriptions. See the
 *  `wgMFDisplayWikibaseDescriptions` configuration variable for detail
 * @return {Object}
 */
module.exports = function extendSearchParams( feature ) {
	// These must be defined, as these are all the features that this can be used on.
	// If not defined, all these features will see their API calls broken
	const displayWikibaseDescriptions = mw.config.get( 'wgMFDisplayWikibaseDescriptions' ) || {
		watchlist: true
	};

	if ( !Object.prototype.hasOwnProperty.call( displayWikibaseDescriptions, feature ) ) {
		throw new Error( '"' + feature + '" isn\'t a feature that shows Wikibase descriptions.' );
	}

	// Construct the arguments for a call to `util.extend`
	// such that if it were hand-written, then it
	// would look like the following:
	//
	// ```
	// var result = util.extend( {
	//   prop: []
	// }, params, /* ..., */ mw.config.get( 'wgMFSearchAPIParams' ) );
	// ```
	const args = Array.prototype.slice.call( arguments, 1 );
	args.unshift( {
		prop: []
	} );
	args.push( mw.config.get( 'wgMFSearchAPIParams' ) );

	const result = util.extend.apply( {}, args );
	result.prop = result.prop.concat( mw.config.get( 'wgMFQueryPropModules' ) );

	if ( displayWikibaseDescriptions[feature] ) {
		if ( !result.prop.includes( 'description' ) ) {
			result.prop.push( 'description' );
		}
	}

	return actionParams( result );
};
/* global $ */
const WatchList = require( './WatchList' ),
	eventBus = require( '../mobile.startup/eventBusSingleton' );

/**
 * Initialises JavaScript on Special:Watchlist
 *
 * @private
 */
function init() {
	const $watchlist = $( 'ul.mw-mf-watchlist-page-list' );

	// FIXME: find more elegant way to not show watchlist stars on recent changes
	if ( $( '.mw-mf-watchlist-selector' ).length === 0 ) {
