const Button = require( './Button.js' );

// Expose chunk to temporary variable which will be deleted and exported via ResourceLoader
// package inside mobile.startup.exports.

/**
 * The main library for accessing MobileFrontend's stable APIs.
 *
 * @module mobile.startup
 */
module.exports = {
	/**
	 * Internal, strictly for use inside MobileFrontend only
	 *
	 * @private
	 */
	actionParams,
	/**
	 * Internal, strictly for use inside MobileFrontend only
	 *
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/util
	 * @private
	 */
	util,
	/**
	 * @private
	 */
	headers,
	// Internal for use inside GrowthExperiments only.
	overlayHeader: headers.header,
	/**
	 * Internal for use inside Minerva, GrowthExperiments only.
	 *
	 * @type module:mobile.startup/Drawer
	 */
	Drawer,
	// Internal for use inside Minerva only.
	CtaDrawer,
	/**
	 * @internal for use strictly inside MobileFrontend only. Other extensions
	 *  should use View.make and Overlay.make
	 */
	class: {
		View,
		Overlay,
		Section,
		MessageBox,
		Icon,
		IconButton,
		Button
	},
	/**
	 * @stable for use
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/View
	 */
	View: {
		make: View.make
	},
	/**
	 * @stable for use
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/Overlay
	 */
	Overlay: {
		make: Overlay.make
	},
	/**
	 * Internal for use inside Minerva only.
	 *
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/PageHTMLParser
	 */
	currentPageHTMLParser,
	/**
	 * Internal for use inside Minerva, ExternalGuidance and Echo only.
	 *
	 * @memberof module:mobile.startup
	 * @return {module:mobile.startup/OverlayManager}
	 */
	getOverlayManager: () => OverlayManager.getSingleton(),
	/**
	 * Internal for use inside Minerva only.
	 *
	 * @type module:mobile.startup/Page
	 * @memberof module:mobile.startup
	 */
	currentPage,
	/**
	 * Internal for use inside Minerva only.
	 *
	 * @type module:mobile.startup/PageHTMLParser
	 * @memberof module:mobile.startup
	 */
	PageHTMLParser,
	/**
	 * Internal for use inside Minerva only.
	 *
	 * @type module:mobile.startup/Icon
	 * @memberof module:mobile.startup
	 */
	spinner: icons.spinner,
	/**
	 * Internal for use inside MobileFrontend only
	 *
	 * @private
	 */
	cancelIcon: icons.cancel,
	/**
	 * Internal for use inside Minerva only.
	 *
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/mediaViewer
	 */
	mediaViewer,
	/**
	 * Internal for use inside Minerva only.
	 *
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/references
	 */
	references,
	/**
	 * Internal for use inside Minerva only.
	 *
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/time
	 */
	time,
	// Internal for use inside Echo, GrowthExperiments only.
	promisedView,
	/**
	 * Loads all images on the page, stable to call.
	 *
	 * @memberof module:mobile.startup
	 * @return {jQuery.Deferred}
	 */
	loadAllImagesInPage: () => lazyImageLoader.loadImages(
		lazyImageLoader.queryPlaceholders( document.getElementById( 'content' ) )
	),
	/**
	 * Show a notification on page reload, internal for Minerva
	 *
	 * @memberof module:mobile.startup
	 * @param {string} msg
	 * @return {jQuery.Deferred}
	 */
	notifyOnPageReload: ( msg ) => showOnPageReload( msg ),
	/**
	 * Internal for use inside VisualEditor
	 *
	 * @memberof module:mobile.startup
	 * @return {string|undefined}
	 */
	license() {
		const skin = Skin.getSingleton();
		return skin.getLicenseMsg();
	},
	/**
	 * Internal for use inside Minerva. See {@link module:mobile.startup} for access.
	 *
	 * @module mobile.startup/languages
	 */
	/**
	 * Access to language overlays for usage inside Minerva only.
	 *
	 * @memberof module:mobile.startup
	 * @type module:mobile.startup/languages
	 */
	languages: {
		languageOverlay,
		/**
		 * Shows information about suggested languages.
		 *
		 * @memberof module:mobile.startup/languages
		 * @param {mw.Api} api
		 * @param {boolean} showSuggestedLanguage If the suggested languages section
		 * should be rendered.
		 */
		languageInfoOverlay( api, showSuggestedLanguage ) {
			return languageInfoOverlay( new LanguageInfo( api ), showSuggestedLanguage );
		}
	}
};
const
	icons = require( './icons' ),
	View = require( './View' );

/**
 * Internal for use inside Echo, GrowthExperiments only.
 * It's a view that spins until the promise resolves!
 * If the promise successfully resolves, the newView will be shown. if the
 * promise rejects and rejects to a view, the errorView will be shown.
 *
 * @function promisedView
 * @memberof module:mobile.startup
 * @param {jQuery.Promise} promise
 * @return {module:mobile.startup/View}
 */
module.exports = function promisedView( promise ) {
	const view = new View( {
		className: 'promised-view'
	} );
	view.$el.append( icons.spinner().$el );
	promise.then( ( newView ) => {
		view.$el.replaceWith( newView.$el );
		// update the internal reference.
		view.$el = newView.$el;
	}, ( errorView ) => {
		if ( !errorView || !errorView.$el ) {
			// return early to keep backwards compatibility with clients of
			// promisedView that do not reject to an error view
			return;
		}

		view.$el.replaceWith( errorView.$el );
		// update the internal reference.
		view.$el = errorView.$el;
	} );

	return view;
};
/**
 * Abstract base class
 * Gateway for retrieving references
 *
 * @class module:mobile.startup/references~Gateway
 * @abstract
 */
class ReferencesGateway {
	/**
	 * @param {mw.Api} api
	 */
	constructor( api ) {
		this.api = api;
	}

	// eslint-disable-next-line jsdoc/require-returns-check
	/**
	 * Return the matched reference via API or DOM query
	 *
	 * @memberof module:mobile.startup/references~Gateway
	 * @instance
	 * @param {string} id CSS selector
	 * @param {Page} page to find reference for
	 * @param {module:mobile.startup/PageHTMLParser} pageHTMLParser
	 * @return {jQuery.Promise} resolves with an Object representing reference
	 *  with a `text` property
	 *  The promise should be rejected with ReferenceGateway.ERROR_NOT_EXIST:
	 *  if the reference is not found.
	 *  If for some reason locating the reference fails return ReferenceGateway.ERROR_OTHER.
	 */
	// eslint-disable-next-line no-unused-vars
	getReference( id, page, pageHTMLParser ) {
		// overridden
	}
}

/**
 * @memberof module:mobile.startup/references~Gateway
 * @property ERROR_NOT_EXIST error code to be returned by getReference
 *  when a reference does not exist.
 * @type string
 */
ReferencesGateway.ERROR_NOT_EXIST = 'NOT_EXIST_ERROR';
/**
 * ERROR_OTHER error code to be returned by getReference
 *  under any other circumstance not covered
 *  by ERROR_NOT_EXIST. It should be used if it is unclear whether a reference exists or not.
 *
 * @memberof module:mobile.startup/references~Gateway
 * @type string
 */
ReferencesGateway.ERROR_OTHER = 'OTHER_ERROR';

module.exports = ReferencesGateway;
const ReferencesGateway = require( './ReferencesGateway' ),
	util = require( './../util' );

/**
 * Gateway for retrieving references via the content of the Page
 *
 * @memberof module:mobile.startup/references
 * @inheritdoc
 */
class ReferencesHtmlScraperGateway extends ReferencesGateway {
	constructor() {
		super( arguments );
		/**
		 * @memberof ReferencesHtmlScraperGateway
		 * @property EXTERNAL_LINK_CLASS a CSS class to place on external links
		 * in addition to the default 'external' class.
		 */
		this.EXTERNAL_LINK_CLASS = 'external--reference';
	}

	/**
	 * @param {string} id ID of a DOM element in the page.
	 * @param {jQuery.Object} $container to scan for an element
	 * @return {jQuery.Promise} that can be used by getReference
	 */
	getReferenceFromContainer( id, $container ) {
		const result = util.Deferred();

		const $el = $container.find( '#' + util.escapeSelector( id ) );
		if ( $el.length ) {
			let $parent;

			// This finds either the inner <ol class="mw-extended-references">, or the outer
			// <ol class="references">
			const $ol = $el.closest( 'ol' );
			const isSubref = $ol.hasClass( 'mw-subreference-list' );
			if ( isSubref ) {
				$parent = $ol.parent();
			}
			// The following classes are used here:
			// * external--reference
			// * other values of EXTERNAL_LINK_CLASS in sub-classes
			( $parent || $el ).find( '.external' ).addClass( this.EXTERNAL_LINK_CLASS );
			result.resolve( {
				text: this.getReferenceHtml( $el ),
				parentText: this.getReferenceHtml( $parent ),
				isSubref
			} );
		} else {
			result.reject( ReferencesGateway.ERROR_NOT_EXIST );
		}
		return result.promise();
	}

	/**
	 * @memberof ReferencesHtmlScraperGateway
	 * @param {jQuery.Object|undefined} $reference
	 * @return {string|undefined}
	 */
	getReferenceHtml( $reference ) {
		return $reference ?
			$reference.children( '.mw-reference-text, .reference-text' ).first().html() :
			'';
	}

	/**
	 * @inheritdoc
	 * @param {string} hash Hash fragment with leading '#'
	 * @param {Page} page (unused)
	 * @param {module:mobile.startup/PageHTMLParser} pageHTMLParser
	 */
	getReference( hash, page, pageHTMLParser ) {
		const id = mw.util.percentDecodeFragment( hash.slice( 1 ) );
		// If an id is not found it's possible the id passed needs decoding (per T188547).
		return this.getReferenceFromContainer( id, pageHTMLParser.$el.find( 'ol.references' ) );
	}
}

module.exports = ReferencesHtmlScraperGateway;
const Drawer = require( '../Drawer' ),
	util = require( '../util' ),
	icons = require( '../icons' ),
	ReferencesGateway = require( './ReferencesGateway' ),
	Icon = require( '../Icon' ),
	ReferencesHtmlScraperGateway = require( './ReferencesHtmlScraperGateway' ),
	IconButton = require( '../IconButton' );

/**
 * Create a callback for clicking references
 *
 * @ignore
 * @param {Function} onNestedReferenceClick
 * @return {Function}
 */
function makeOnNestedReferenceClickHandler( onNestedReferenceClick ) {
	return ( ev ) => {
		const target = ev.currentTarget.querySelector( 'a' );
		if ( target ) {
			onNestedReferenceClick(
				target.getAttribute( 'href' ),
				target.textContent
			);
			// Don't hide the already shown drawer via propagation
			return false;
		}
	};
}

/**
 * Drawer for references
 *
 * @memberof module:mobile.startup/references
 * @uses IconButton
 * @param {Object} props
 * @param {boolean} [props.error] whether an error has occurred
 * @param {string} props.title of reference e.g [1]
 * @param {string} props.text is the HTML of the reference
 * @param {string} [props.parentText] is the HTML of the parent reference if there is one
 * @param {boolean} props.isSubref true when this reference has a parent
 * @param {Function} [props.onNestedReferenceClick] callback for when a reference
 *  inside the reference is clicked.
 * @return {module:mobile.startup/Drawer}
 */
function referenceDrawer( props ) {
	const errorIcon = props.error ? new IconButton( {
		name: 'error',
		isSmall: true
	} ).$el : null;

	const mainRef = props.isSubref ? props.parentText : props.text;
	const mainRefHtml = util.parseHTML( '<div>' )
		.addClass( 'main-reference-content' )
		.html( mainRef );
	if ( !mainRef ) {
		mainRefHtml.append( icons.spinner().$el );
	}
	const subRefHtml = props.isSubref ?
		util.parseHTML( '<div>' ).html( props.text ) : '';

	return new Drawer(
		util.extend(
			{
				showCollapseIcon: false,
				className: 'drawer position-fixed text references-drawer',
				events: {
					'click sup a': ( ev ) => {
						// Stop default scroll to hash fragment behaviour.
						ev.preventDefault();
					},
					'click sup': props.onNestedReferenceClick &&
						makeOnNestedReferenceClickHandler( props.onNestedReferenceClick )
				},
				children: [
					util.parseHTML( '<div>' )
						.addClass( 'references-drawer__header' )
						.append( [
							new Icon( {
								icon: 'reference',
								isSmall: true
							} ).$el,
							util.parseHTML( '<span>' ).addClass( 'references-drawer__title' ).text( mw.msg( 'mobile-frontend-references-citation' ) ),
							icons.cancel( 'gray', {
								isSmall: true
							} ).$el
						] ),

					// Add .mw-parser-output so that TemplateStyles styles apply (T244510)
					util.parseHTML( '<div>' ).addClass( 'mw-parser-output' ).append( [
						errorIcon,
						util.parseHTML( '<sup>' ).text( props.title ),
						mainRefHtml,
						subRefHtml
					] )
				]
			},
			props
		)
	);
}

/**
 * Internal for use inside Minerva only. See {@link module:mobile.startup} for access.
 *
 * @exports module:mobile.startup/references
 */
const references = {
	test: {
		makeOnNestedReferenceClickHandler
	},
	ReferencesHtmlScraperGateway,
	referenceDrawer,
	/**
