const util = require( './util.js' ),
	actionParams = require( './actionParams' );

/**
 * API for providing language data.
 *
 * @class module:mobile.startup/languages~LanguageInfo
 */
class LanguageInfo {
	/**
	 * @param {mw.Api} api
	 */
	constructor( api ) {
		this.api = api;
	}

	/**
	 * Get languageinfo API data from the local wiki, and transform it into a
	 * format usable by LanguageSearcher.
	 *
	 * @memberof module:mobile.startup/languages~LanguageInfo
	 * @instance
	 * @returns {jQuery.Deferred}
	 */
	getLanguages() {
		return this.api.get( actionParams( {
			meta: 'languageinfo',
			liprop: 'code|autonym|name|bcp47'
		} ) ).then( ( resp ) => {
			const filteredLanguages = [];
			// Filter out legacy languages and require an autonym.
			// If the bcp47 (https://w.wiki/Y7A) does not match the language
			// code, that is in an indication that the language code is outdated
			// and should not be used.
			Object.keys( resp.query.languageinfo ).forEach( ( key ) => {
				const language = resp.query.languageinfo[key];
				if ( ( language.code.toLowerCase() === language.bcp47.toLowerCase() ) &&
					language.autonym ) {
					filteredLanguages.push( language );
				}
			} );
			return filteredLanguages;
		}, () => util.Deferred().reject() ).then( ( filteredLanguages ) => ( {
			languages: filteredLanguages.map( ( data ) => {
				data.url = '#';
				data.lang = data.code;
				data.langname = data.name;
				// FIXME: This isn't a "title" in the sense of a MediaWiki
				// Title, and it is rendered as a subheader in the list
				// item, so a different name would be wiser, both here and
				// in LanguageSearcher's template. Also it would arguably
				// be more intuitive for the language name (localized) to
				// appear as the main emphasized element of each language
				// list element; but instead the autonym has that role.
				// A more thorough refactoring of LanguageSearcher to allow
				// for generic header/subheader elements is left for a
				// follow-up.
				data.title = data.name;
				return data;
			} )
		} ), () => util.Deferred().reject() );
	}
}

module.exports = LanguageInfo;
const
	View = require( './View' ),
	util = require( './util' );

/**
 * Render CSS version of Codex message component.
 */
class MessageBox extends View {
	/**
	 * @inheritdoc
	 */
	constructor( props ) {
		super( props );
	}

	/**
	 * @inheritdoc
	 */
	get isTemplateMode() {
		return true;
	}

	/**
	 * @mixes module:mobile.startup/View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} [defaults.heading] heading to show along with message (text)
	 * @property {string} defaults.msg message to show (html)
	 * @property {string} defaults.type either error, notice or warning
	 * @property {string} defaults.className
	 */
	get defaults() {
		return {};
	}

	get template() {
		return util.template( `
<div
  class="cdx-message cdx-message--block cdx-message--{{type}} {{className}}"
  aria-live="polite"
>
  <!-- Empty span for message icon. -->
  <span class="cdx-message__icon"></span>
  <!-- Div for content. -->
  <div class="cdx-message__content">
  {{#heading}}<h2>{{heading}}</h2>{{/heading}}
  {{{msg}}}
  </div>
</div>
	` );
	}
}

module.exports = MessageBox;
const util = require( './util.js' ),
	View = require( './View' );

/**
 * Builds a section of a page
 */
class Section extends View {
	/**
	 * @param {Object} options Configuration options
	 */
	constructor( options ) {
		options.tag = 'h' + options.level;
		super( options );
		this.line = options.line;
		this.text = options.text;
		this.hasReferences = options.hasReferences || false;
		this.id = options.id || null;
		this.anchor = options.anchor;
		this.subsections = [];
		( options.subsections || [] ).forEach( ( section ) => this.subsections.push(
			new Section( section ) ) );
	}

	get template() {
		return util.template( `
<h{{level}} id="{{anchor}}">{{{line}}}</h{{level}}>
{{{text}}}
	` );
	}

	/**
	 * @mixes module:mobile.startup/View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.text Section text.
	 */
	get defaults() {
		return {
			line: undefined,
			text: ''
		};
	}
}

module.exports = Section;
/**
 * Return the language code of the device in lowercase
 *
 * @ignore
 * @param {window.navigator} navigator
 * @return {string|undefined}
 */
function getDeviceLanguage( navigator ) {
	const lang = navigator.languages ?
		navigator.languages[ 0 ] :
		navigator.language || navigator.userLanguage ||
				navigator.browserLanguage || navigator.systemLanguage;

	return lang ? lang.toLowerCase() : undefined;
}

module.exports = getDeviceLanguage;
const
	m = require( '../moduleLoaderSingleton' ),
	getDeviceLanguage = require( './getDeviceLanguage' ),
	Overlay = require( '../Overlay' ),
	promisedView = require( '../promisedView' );

/**
 * @ignore
 * @param {LanguageInfo} languageInfo
 * @param {boolean} showSuggestedLanguages If the suggested languages section
 * should be rendered.
 * @return {jQuery.Promise} Resolves to LanguageSearcher
 */
function loadLanguageInfoSearcher( languageInfo, showSuggestedLanguages ) {
	return mw.loader.using( 'mobile.languages.structured' ).then( () => languageInfo.getLanguages() ).then( ( data ) => {
		const LanguageSearcher = m.require( 'mobile.languages.structured/LanguageSearcher' );
		return new LanguageSearcher( {
			languages: data.languages,
			variants: data.variants,
			showSuggestedLanguages,
			deviceLanguage: getDeviceLanguage( navigator )
		} );

	} );
}

/**
 * Factory function that returns a language info featured instance of an Overlay
 *
 * @ignore
 * @function
 * @memberof module:mobile.startup/languages
 * @param {module:mobile.startup/languages~LanguageInfo} languageInfo
 * @param {boolean} showSuggestedLanguages If the suggested languages section
 * should be rendered.
 * @return {module:mobile.startup/Overlay}
 */
function languageInfoOverlay( languageInfo, showSuggestedLanguages ) {
	return Overlay.make(
		{
			heading: mw.msg( 'mobile-frontend-language-heading' ),
			className: 'overlay language-info-overlay'
		}, promisedView( loadLanguageInfoSearcher( languageInfo, showSuggestedLanguages ) )
	);
}

// To make knowing when async logic has resolved easier in tests
languageInfoOverlay.test = {
	loadLanguageInfoSearcher
};

module.exports = languageInfoOverlay;
const
	m = require( '../moduleLoaderSingleton' ),
	getDeviceLanguage = require( './getDeviceLanguage' ),
	Overlay = require( '../Overlay' ),
	MessageBox = require( '../MessageBox' ),
	currentPageHTMLParser = require( '../currentPageHTMLParser' )(),
	promisedView = require( '../promisedView' );

/**
 * @ignore
 * @return {jQuery.Promise} Resolves to LanguageSearcher
 */
function loadLanguageSearcher() {
	return mw.loader.using( 'mobile.languages.structured' ).then( () => currentPageHTMLParser.getLanguages(
		mw.config.get( 'wgTitle' )
	) ).then( ( data ) => {
		const LanguageSearcher = m.require( 'mobile.languages.structured/LanguageSearcher' );

		return new LanguageSearcher( {
			languages: data.languages,
			variants: data.variants,
			showSuggestedLanguages: true,
			deviceLanguage: getDeviceLanguage( navigator ),
			/**
			 * Stable for use inside ContentTranslation.
			 *
			 * @event ~'mobileFrontend.languageSearcher.onOpen'
			 * @memberof Hooks
			 * @param {Hooks~LanguageSearcher} searcher
			 */
			onOpen: ( searcher ) => mw.hook( 'mobileFrontend.languageSearcher.onOpen' ).fire( searcher )
		} );
	}, () => new MessageBox( {
		type: 'error',
		className: 'content',
		msg: mw.msg( 'mobile-frontend-languages-structured-overlay-error' )
	} ) );
}

/**
 * Factory function that returns a language featured instance of an Overlay.
 *
 * @function
 * @memberof module:mobile.startup/languages
 * @return {module:mobile.startup/Overlay}
 */
function languageOverlay() {
	return Overlay.make(
		{
			heading: mw.msg( 'mobile-frontend-language-heading' ),
			className: 'overlay language-overlay'
		}, promisedView( loadLanguageSearcher() )
	);
}

// To make knowing when async logic has resolved easier in tests
languageOverlay.test = {
	loadLanguageSearcher
};

module.exports = languageOverlay;
/**
 * Internal for use inside Minerva only. See {@link module:mobile.startup} for access.
 *
 * @module mobile.startup/mediaViewer
 */
const m = require( '../moduleLoaderSingleton' ),
	promisedView = require( '../promisedView' ),
