var
	mfExtend = require( '../mobile.startup/mfExtend' ),
	Overlay = require( '../mobile.startup/Overlay' ),
	PageGateway = require( '../mobile.startup/PageGateway' ),
	util = require( '../mobile.startup/util' ),
	autosign = require( './autosign' ),
	toast = require( '../mobile.startup/toast' ),
	Icon = require( '../mobile.startup/Icon' );

/**
 * Overlay for adding a talk section
 * @class TalkSectionAddOverlay
 * @extends Overlay
 * @uses Toast
 *
 * @param {Object} options Configuration options
 * @param {Object} options.title Title of the talk page being modified
 * @param {Object} options.currentPageTitle Title of the page before the overlay appears
 * @param {OO.EventEmitter} options.eventBus Object used to emit talk-added-wo-overlay
 * and talk-discussion-added events
 */
function TalkSectionAddOverlay( options ) {
	this.editorApi = options.api;
	this.pageGateway = new PageGateway( options.api );
	Overlay.call( this,
		util.extend( options, {
			className: 'talk-overlay overlay',
			events: {
				'input .wikitext-editor, input': 'onTextInput',
				'change .wikitext-editor, input': 'onTextInput',
				'click .confirm-save': 'onSaveClick'
			}
		} )
	);
	this.title = options.title;
	this.currentPageTitle = options.currentPageTitle;
	this.eventBus = options.eventBus;
	// Variable to indicate, if the overlay will be closed by the save function
	// or by the user. If this is false and there is content in the input fields,
	// the user will be asked, if he want to abandon his changes before we close
	// the Overlay, otherwise the Overlay will be closed without any question.
	this._saveHit = false;
}

mfExtend( TalkSectionAddOverlay, Overlay, {
	/**
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 * @mixes Overlay#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.cancelMsg Caption for cancel button on edit form.
	 * @property {string} defaults.topicTitlePlaceHolder Placeholder text to prompt user to add
	 * a talk page topic subject.
	 * @property {string} defaults.topicContentPlaceHolder Placeholder text to prompt user
	 *  to add content to talk page content.
	 * @property {string} defaults.editingMsg Label for button which
	 *  submits a new talk page topic.
	 */
	defaults: util.extend( {}, Overlay.prototype.defaults, {
		cancelMsg: mw.msg( 'mobile-frontend-editor-cancel' ),
		topicTitlePlaceHolder: mw.msg( 'mobile-frontend-talk-add-overlay-subject-placeholder' ),
		topicContentPlaceHolder: mw.msg( 'mobile-frontend-talk-add-overlay-content-placeholder' ),
		editingMsg: mw.msg( 'mobile-frontend-talk-add-overlay-submit' ),
		waitMsg: mw.msg( 'mobile-frontend-talk-topic-wait' ),
		// icons.spinner can't be used, .loading has a fixed height, which breaks overlay-header
		waitIcon: new Icon( {
			name: 'spinner',
			additionalClassNames: 'savespinner loading'
		} ).toHtmlString()
	} ),
	/**
	 * @inheritdoc
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 */
	template: mw.template.get( 'mobile.talk.overlays', 'SectionAddOverlay.hogan' ),
	/**
	 * @inheritdoc
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 */
	templatePartials: util.extend( {}, Overlay.prototype.templatePartials, {
		contentHeader: mw.template.get( 'mobile.talk.overlays', 'SectionAddOverlay/contentHeader.hogan' ),
		saveHeader: mw.template.get( 'mobile.editor.overlay', 'saveHeader.hogan' )
	} ),
	/**
	 * @inheritdoc
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 */
	postRender: function () {
		Overlay.prototype.postRender.call( this );
		this.showHidden( '.initial-header' );
		this.$confirm = this.$el.find( 'button.confirm-save' );
		this.$subject = this.$el.find( 'input' );
		this.$ta = this.$el.find( '.wikitext-editor' );
	},
	/**
	 * @inheritdoc
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 */
	hide: function () {
		var empty,
			confirmMessage = mw.msg( 'mobile-frontend-editor-cancel-confirm' );

		empty = ( !this.$subject.val() && !this.$ta.val() );
		// TODO: Replace with an OOUI dialog
		if ( this._saveHit || empty || window.confirm( confirmMessage ) ) {
			return Overlay.prototype.hide.apply( this, arguments );
		} else {
			return false;
		}
	},
	/**
	 * Handles an input into a textarea and enables or disables the submit button
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 */
	onTextInput: function () {
		var self = this;

		clearTimeout( this.timer );
		this.timer = setTimeout( function () {
			if ( !self.$ta.val().trim() || !self.$subject.val().trim() ) {
				self.$confirm.prop( 'disabled', true );
			} else {
				self.$confirm.prop( 'disabled', false );
			}
		}, 250 );
	},
	/**
	 * Handles a click on the save button
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 */
	onSaveClick: function () {
		var self = this,
			isOnTalkPage = self.title === self.currentPageTitle;

		this.showHidden( '.saving-header' );
		this.save().then( function ( status ) {
			if ( status === 'ok' ) {
				if ( isOnTalkPage ) {
					self.eventBus.emit( 'talk-added-wo-overlay' );
				} else {
					self.pageGateway.invalidatePage( self.title );
					toast.show( mw.msg( 'mobile-frontend-talk-topic-feedback' ) );
					self.eventBus.emit( 'talk-discussion-added' );
					self.hide();
				}
			}
		}, function ( error ) {
			var editMsg = mw.msg( 'mobile-frontend-talk-topic-error' );

			self.$confirm.prop( 'disabled', false );
			switch ( error.details ) {
				case 'protectedpage':
					editMsg = mw.msg( 'mobile-frontend-talk-topic-error-protected' );
					break;
				case 'noedit':
				case 'blocked':
					editMsg = mw.msg( 'mobile-frontend-talk-topic-error-permission' );
					break;
				case 'spamdetected':
					editMsg = mw.msg( 'mobile-frontend-talk-topic-error-spam' );
					break;
				case 'badtoken':
					editMsg = mw.msg( 'mobile-frontend-talk-topic-error-badtoken' );
					break;
				default:
					editMsg = mw.msg( 'mobile-frontend-talk-topic-error' );
					break;
			}

			toast.show( editMsg, { type: 'error' } );
			self.showHidden( '.save-header, .save-panel' );
		} );
	},
	/**
	 * Save new talk section
	 * @memberof TalkSectionAddOverlay
	 * @instance
	 * @return {jQuery.Deferred} Object that either will be resolved with ok parameter
	 * or rejected with type error.
	 */
	save: function () {
		var heading = this.$subject.val(),
			self = this,
			d = util.Deferred(),
			text = this.$ta.val();

		this.$ta.removeClass( 'error' );
		this.$subject.removeClass( 'error' );

		// propagate, that we save an edit and want to close the Overlay without
		// any interruption (user questions e.g.)
		this._saveHit = true;

		this.$el.find( '.content' ).empty().addClass( 'loading' );
		// FIXME: while saving: a spinner would be nice
		// FIXME: This should be using a gateway e.g. TalkGateway, PageGateway or EditorGateway
		return this.editorApi.postWithToken( 'csrf', {
			action: 'edit',
			section: 'new',
			sectiontitle: heading,
			title: self.title,
			summary: mw.msg( 'newsectionsummary', heading ),
			text: autosign( text )
		} ).then( function () {
			return 'ok';
		}, function ( msg ) {
			// FIXME: Throw an Error
			return d.reject( {
				type: 'error',
				details: msg
			} );
		} );
	}
} );

module.exports = TalkSectionAddOverlay;
var
	user = mw.user,
	mfExtend = require( '../mobile.startup/mfExtend' ),
	PageGateway = require( '../mobile.startup/PageGateway' ),
	Overlay = require( '../mobile.startup/Overlay' ),
	util = require( '../mobile.startup/util' ),
	popup = require( '../mobile.startup/toast' ),
	autosign = require( './autosign' ),
	Page = require( '../mobile.startup/Page' ),
	Button = require( '../mobile.startup/Button' );

/**
 * Overlay for showing talk page section
 * @class TalkSectionOverlay
 * @extends Overlay
 * @uses PageGateway
 * @uses Page
 * @uses Button
 * @uses Toast
 * @param {Object} options
 */
function TalkSectionOverlay( options ) {
	this.editorApi = options.api;
	this.pageGateway = new PageGateway( options.api );
	Overlay.call( this,
		util.extend( true, options, {
			className: 'talk-overlay overlay',
			events: {
				'focus textarea': 'onFocusTextarea',
				'click .save-button': 'onSaveClick'
			}
		} )
	);
}

mfExtend( TalkSectionOverlay, Overlay, {
	templatePartials: util.extend( {}, Overlay.prototype.templatePartials, {
		header: mw.template.get( 'mobile.talk.overlays', 'Section/header.hogan' ),
		content: mw.template.get( 'mobile.talk.overlays', 'Section/content.hogan' )
	} ),
	/**
	 * @memberof TalkSectionOverlay
	 * @instance
	 * @mixes Overlay#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.title Title.
	 * @property {Section} defaults.section that is currently being viewed in overlay.
	 * @property {string} defaults.reply Reply heading.
	 * @property {string} defaults.info Message that informs the user their talk reply will be
	 * automatically signed.
	 */
	defaults: util.extend( {}, Overlay.prototype.defaults, {
		saveButton: new Button( {
			block: true,
			additionalClassNames: 'save-button',
			progressive: true,
			label: mw.config.get( 'wgEditSubmitButtonLabelPublish' ) ?
				mw.msg( 'mobile-frontend-editor-publish' ) : mw.msg( 'mobile-frontend-editor-save' )
		} ).options,
		title: undefined,
		section: undefined,
		reply: mw.msg( 'mobile-frontend-talk-reply' ),
		info: mw.msg( 'mobile-frontend-talk-reply-info' )
	} ),
	/**
	 * Fetches the talk topics of the page specified in options.title
	 * if options.section is not defined.
	 * @inheritdoc
	 * @memberof TalkSectionOverlay
	 * @instance
	 */
	postRender: function () {
		Overlay.prototype.postRender.apply( this );
		this.$saveButton = this.$el.find( '.save-button' );
		if ( !this.options.section ) {
			this.renderFromApi( this.options );
		} else {
			this.hideSpinner();
			this._enableComments();
		}
	},
	/**
	 * Enables comments on the current rendered talk topic
	 * @memberof TalkSectionOverlay
	 * @instance
	 * @private
	 */
	_enableComments: function () {
		this.$commentBox = this.$el.find( '.comment' );
		if ( user.isAnon() ) {
			this.$commentBox.remove();
		} else {
			this.$textarea = this.$commentBox.find( 'textarea' );
		}
	},
	/**
	 * Loads the discussion from api and add it to the Overlay
	 * @memberof TalkSectionOverlay
	 * @instance
	 * @param {Object} options Render options
	 */
	renderFromApi: function ( options ) {
		var self = this;

		this.pageGateway.getPage( options.title ).then( function ( pageData ) {
			var page = new Page( pageData );
			options.section = page.getSection( options.id );
			self.render( options );
			self.hideSpinner();
		} );
	},
	/**
	 * Handler for focus of textarea
	 * @memberof TalkSectionOverlay
	 * @instance
	 */
	onFocusTextarea: function () {
		this.$textarea.removeClass( 'error' );
	},
	/**
	 * Handle a click on the save button
	 * @memberof TalkSectionOverlay
	 * @instance
	 */
	onSaveClick: function () {
		var val = this.$textarea.val(),
			self = this;

		function enableSaveButton() {
			self.$saveButton.prop( 'disabled', false );
		}
		if ( val ) {
			// show a spinner
			this.showSpinner();
			this.$saveButton.prop( 'disabled', true );
			// sign and add newline to front
			val = '\n\n' + autosign( val );
			// FIXME: This should be using a gateway
			// e.g. TalkGateway, PageGateway or EditorGateway
			this.editorApi.postWithToken( 'csrf', {
				action: 'edit',
				title: this.options.title,
				section: this.options.id,
				appendtext: val,
				redirect: true
			} ).then( function () {
				popup.show( mw.msg( 'mobile-frontend-talk-reply-success' ) );
				// invalidate the cache
				self.pageGateway.invalidatePage( self.options.title );

				self.renderFromApi( self.options );

				enableSaveButton();
			}, function ( data, response ) {
				// FIXME: Code sharing with EditorOverlay?
				var msg,
					// When save failed with one of these error codes, the returned
					// message in response.error.info will be forwarded to the user.
					// FIXME: This shouldn't be needed when info texts are all localized.
					whitelistedErrorInfo = [
						'readonly',
						'blocked',
						'autoblocked'
					];

				if (
					response.error &&
					whitelistedErrorInfo.indexOf( response.error.code ) > -1
				) {
					msg = response.error.info;
				} else {
					msg = mw.msg( 'mobile-frontend-editor-error' );
				}

				self.hideSpinner();
				popup.show( msg, 'toast error' );

				enableSaveButton();
			} );
		} else {
			this.$textarea.addClass( 'error' );
		}
	}
} );

module.exports = TalkSectionOverlay;
/**
 * Autosign a block of text if necessary
 * @instance
 * @param {string} text
 * @return {string} text with an autosign ("~~~~") if necessary
 */
function autosign( text ) {
	return /~{3,5}/.test( text ) ? text : text + ' ~~~~';
}

module.exports = autosign;
var m = require( './../mobile.startup/moduleLoaderSingleton' ),
	talkBoard = require( './talkBoard' ),
	overlay = require( '../mobile.startup/talk/overlay' ),
	PageGateway = require( '../mobile.startup/PageGateway' ),
	TalkSectionAddOverlay = require( './TalkSectionAddOverlay' ),
	TalkSectionOverlay = require( './TalkSectionOverlay' );

/**
 * Backwards compatible method for obtaining a TalkOverlay
 * used by Minerva until it updates itself.
 * @deprecated
 * @param {Object} options
 * @return {Overlay}
 */
function talkOverlay( options ) {
	return overlay( options.title, new PageGateway( options.api ) );
}

m.deprecate( 'mobile.talk.overlays/talkOverlay', talkOverlay,
	'Use `mobile.startup` (talk.overlay)' );
m.define( 'mobile.talk.overlays/talkBoard', talkBoard );
m.define( 'mobile.talk.overlays/TalkSectionAddOverlay', TalkSectionAddOverlay );
m.define( 'mobile.talk.overlays/TalkSectionOverlay', TalkSectionOverlay ); // resource-modules-disable-line
var util = require( '../mobile.startup/util' ),
	View = require( '../mobile.startup/View' );

/**
 * A board of talk topics
 * @param {Section[]} sections
 * @return {View}
 */
function talkBoard( sections ) {
	var board,
		explanation = sections.length > 0 ?
			mw.msg( 'mobile-frontend-talk-explained' ) :
			mw.msg( 'mobile-frontend-talk-explained-empty' );

	board = new View( {
		className: 'talk-board'
	} );
	board.append( [
		util.parseHTML( '<p class="content-header">' ).text( explanation ),
		// FIXME: Substitute with a TopicTitleList (T173534)
		util.parseHTML( '<ul class="topic-title-list">' ).append(
			sections.map( function ( section ) {
				return util.parseHTML( '<li>' ).append(
					util.parseHTML( '<a>' )
						.attr( 'href', '#/talk/' + section.id )
						.text( section.line )
				);
			} )
		)
	] );
	return board;
}

module.exports = talkBoard;
